import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  TextField,
  Button,
  Chip,
  Stack,
  FormControlLabel,
  Checkbox,
  Slider,
  Alert,
} from '@mui/material';
import {
  ExpandMore,
  Add,
  Remove,
  Close,
  FilterList,
  PlayArrow,
} from '@mui/icons-material';
import { useAppStore } from '../store';
import { KeywordData } from '../types';

interface FilterPanelProps {
  data: KeywordData[] | null;
}

interface ColumnFilter {
  column: string;
  min: number;
  max: number;
  currentMin: number;
  currentMax: number;
  display: string;
}

export const FilterPanel: React.FC<FilterPanelProps> = ({ data }) => {
  const {
    filters,
    setColumnFilter,
    addSearchTerm,
    removeSearchTerm,
    addExcludeTerm,
    removeExcludeTerm,
    setFilterNonLatin,
    clearFilters,
  } = useAppStore();

  const [searchInput, setSearchInput] = useState('');
  const [columnFilters, setColumnFilters] = useState<Record<string, ColumnFilter>>({});
  const [pendingFilters, setPendingFilters] = useState<Record<string, ColumnFilter>>({});
  const [hasPendingChanges, setHasPendingChanges] = useState(false);

  // Dinamik olarak sayısal sütunları bul ve filtreleri oluştur
  useEffect(() => {
    if (!data || data.length === 0) return;

    const newColumnFilters: Record<string, ColumnFilter> = {};

    // İlk satırdan sütun isimlerini al
    const firstRow = data[0];
    if (firstRow) {
      Object.keys(firstRow).forEach(column => {
        // Sütundaki tüm değerleri kontrol et
        const values = data
          .map(row => row[column as keyof KeywordData])
          .filter(val => {
            // Sayısal değerleri kontrol et (int64, float64, number)
            if (typeof val === 'number' && !isNaN(val)) {
              return true;
            }
            // String olarak gelen sayıları da kontrol et
            if (typeof val === 'string' && !isNaN(Number(val))) {
              return true;
            }
            return false;
          })
          .map(val => {
            // String sayıları number'a çevir
            return typeof val === 'string' ? Number(val) : val as number;
          });

        if (values.length > 0) {
          const min = Math.min(...values);
          const max = Math.max(...values);

          newColumnFilters[column] = {
            column,
            min,
            max,
            currentMin: min,
            currentMax: max,
            display: `${column}: ${min} - ${max}`,
          };
        }
      });
    }

    setColumnFilters(newColumnFilters);
    setPendingFilters(newColumnFilters);
  }, [data]);

  const handleSearchSubmit = () => {
    if (searchInput.trim()) {
      addSearchTerm(searchInput.trim());
      setSearchInput('');
      setHasPendingChanges(true);
    }
  };

  const handleExcludeSubmit = () => {
    if (searchInput.trim()) {
      addExcludeTerm(searchInput.trim());
      setSearchInput('');
      setHasPendingChanges(true);
    }
  };

  const handleColumnFilterChange = (column: string, value: number[]) => {
    const currentFilter = pendingFilters[column];
    if (currentFilter) {
      const newFilter = {
        ...currentFilter,
        currentMin: value[0],
        currentMax: value[1],
        display: `${column}: ${value[0]} - ${value[1]}`,
      };

      setPendingFilters(prev => ({
        ...prev,
        [column]: newFilter,
      }));

      setHasPendingChanges(true);
    }
  };

  const handleApplyFilters = () => {
    // Tüm pending filtreleri uygula
    Object.values(pendingFilters).forEach(filter => {
      setColumnFilter(filter.column, filter.currentMin, filter.currentMax);
    });

    setHasPendingChanges(false);
  };

  const handleClearFilters = () => {
    clearFilters();
    setPendingFilters(columnFilters);
    setHasPendingChanges(false);
  };

  const handleFilterNonLatinChange = (checked: boolean) => {
    setFilterNonLatin(checked);
    setHasPendingChanges(true);
  };

  return (
    <Box sx={{ width: '100%' }}>
      <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
        <FilterList />
        Filtre Ayarları
      </Typography>

      {/* Filtreleri Uygula Butonu */}
      {hasPendingChanges && (
        <Alert 
          severity="info" 
          sx={{ mb: 2 }}
          action={
            <Button
              variant="contained"
              color="primary"
              size="small"
              startIcon={<PlayArrow />}
              onClick={handleApplyFilters}
            >
              Filtreleri Uygula
            </Button>
          }
        >
          Filtrelerde değişiklik yapıldı. Uygulamak için butona basın.
        </Alert>
      )}

      {/* Sütun Filtreleri */}
      <Accordion defaultExpanded>
        <AccordionSummary expandIcon={<ExpandMore />}>
          <Typography variant="subtitle1" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            📊 Sayısal Sütun Filtreleri
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          {Object.keys(pendingFilters).length === 0 ? (
            <Typography variant="body2" color="text.secondary" sx={{ fontStyle: 'italic' }}>
              Veriler yüklendikten sonra sayısal sütun filtreleri burada görünecek
            </Typography>
          ) : (
            <Stack spacing={3}>
              {Object.entries(pendingFilters).map(([column, filter]) => (
                <Box key={column}>
                  <Typography variant="body2" color="primary" fontWeight="bold" gutterBottom>
                    {filter.column}
                  </Typography>
                  <Typography variant="caption" color="text.secondary" gutterBottom>
                    Aralık: {filter.currentMin} - {filter.currentMax} (Orijinal: {filter.min} - {filter.max})
                  </Typography>
                  <Slider
                    value={[filter.currentMin, filter.currentMax]}
                    onChange={(_, value) => {
                      if (Array.isArray(value)) {
                        handleColumnFilterChange(column, value);
                      }
                    }}
                    min={filter.min}
                    max={filter.max}
                    valueLabelDisplay="auto"
                    sx={{ mt: 1 }}
                  />
                </Box>
              ))}
            </Stack>
          )}
        </AccordionDetails>
      </Accordion>

      {/* Keyword Arama */}
      <Accordion defaultExpanded>
        <AccordionSummary expandIcon={<ExpandMore />}>
          <Typography variant="subtitle1" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            🔍 Keyword Arama
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Stack spacing={2}>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <TextField
                fullWidth
                size="small"
                placeholder="Örn: ai, photo, music"
                value={searchInput}
                onChange={(e) => setSearchInput(e.target.value)}
                onKeyPress={(e) => {
                  if (e.key === 'Enter') {
                    handleSearchSubmit();
                  }
                }}
              />
              <Button
                variant="contained"
                startIcon={<Add />}
                onClick={handleSearchSubmit}
                size="small"
              >
                Ekle
              </Button>
              <Button
                variant="contained"
                color="error"
                startIcon={<Remove />}
                onClick={handleExcludeSubmit}
                size="small"
              >
                Çıkar
              </Button>
            </Box>

            {/* Arama Terimleri */}
            <Box>
              <Typography variant="body2" color="primary" fontWeight="bold" gutterBottom>
                🏷️ Arama Terimleri:
              </Typography>

              {filters.searchTerms.length === 0 && filters.excludeTerms.length === 0 ? (
                <Typography variant="body2" color="text.secondary" sx={{ fontStyle: 'italic' }}>
                  Henüz arama terimi eklenmedi
                </Typography>
              ) : (
                <Stack direction="row" spacing={1} flexWrap="wrap" gap={1}>
                  {/* Dahil edilecek terimler */}
                  {filters.searchTerms.map((term, index) => (
                    <Chip
                      key={`include-${index}`}
                      label={term}
                      color="primary"
                      size="small"
                      onDelete={() => removeSearchTerm(term)}
                      icon={<Add />}
                    />
                  ))}

                  {/* Çıkarılacak terimler */}
                  {filters.excludeTerms.map((term, index) => (
                    <Chip
                      key={`exclude-${index}`}
                      label={term}
                      color="error"
                      size="small"
                      onDelete={() => removeExcludeTerm(term)}
                      icon={<Remove />}
                    />
                  ))}
                </Stack>
              )}
            </Box>
          </Stack>
        </AccordionDetails>
      </Accordion>

      {/* Diğer Filtreler */}
      <Accordion>
        <AccordionSummary expandIcon={<ExpandMore />}>
          <Typography variant="subtitle1" sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
            ⚙️ Diğer Filtreler
          </Typography>
        </AccordionSummary>
        <AccordionDetails>
          <Stack spacing={2}>
            <FormControlLabel
              control={
                <Checkbox
                  checked={filters.filterNonLatin}
                  onChange={(e) => handleFilterNonLatinChange(e.target.checked)}
                  color="warning"
                />
              }
              label="Latin Harici Alfabeleri Çıkar"
            />

            <Button
              variant="outlined"
              color="error"
              onClick={handleClearFilters}
              startIcon={<Close />}
            >
              Tüm Filtreleri Temizle
            </Button>
          </Stack>
        </AccordionDetails>
      </Accordion>
    </Box>
  );
};
