import React, { useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import {
  Box,
  Typography,
  Paper,
  IconButton,
  Chip,
  Stack,
  Switch,
  FormControlLabel,
  Alert,
} from '@mui/material';
import {
  CloudUpload,
  Folder,
  FileCopy,
  Delete,
} from '@mui/icons-material';
import { useAppStore } from '../store';

interface FileUploadProps {
  onFilesSelected: (files: File[]) => void;
}

export const FileUpload: React.FC<FileUploadProps> = ({ onFilesSelected }) => {
  const {
    dateMode,
    fileMode,
    setDateMode,
    setFileMode,
    setError,
    setSuccess,
  } = useAppStore();

  const onDrop = useCallback(
    (acceptedFiles: File[]) => {
      if (acceptedFiles.length === 0) {
        setError('L√ºtfen ge√ßerli dosyalar se√ßin');
        return;
      }

      // Dosya t√ºr√º kontrol√º
      const csvFiles = acceptedFiles.filter(file => 
        file.type === 'text/csv' || file.name.toLowerCase().endsWith('.csv')
      );

      if (csvFiles.length === 0) {
        setError('L√ºtfen CSV dosyalarƒ± se√ßin');
        return;
      }

      if (fileMode) {
        // Tek dosya modu
        if (csvFiles.length > 1) {
          setError('Tek dosya modunda sadece bir CSV dosyasƒ± se√ßebilirsiniz');
          return;
        }
        setSuccess(`CSV dosyasƒ± se√ßildi: ${csvFiles[0].name}`);
      } else if (dateMode) {
        // Tarih modu - klas√∂r yapƒ±sƒ± kontrol et
        const folderStructure = groupFilesByFolder(csvFiles);
        if (Object.keys(folderStructure).length === 0) {
          setError('Tarih modu i√ßin klas√∂r yapƒ±sƒ± gerekli');
          return;
        }
        setSuccess(`${Object.keys(folderStructure).length} klas√∂r, ${csvFiles.length} CSV dosyasƒ± se√ßildi`);
      } else {
        // Normal mod
        setSuccess(`${csvFiles.length} CSV dosyasƒ± se√ßildi`);
      }

      onFilesSelected(csvFiles);
    },
    [fileMode, dateMode, onFilesSelected, setError, setSuccess]
  );

  const { getRootProps, getInputProps, isDragActive, acceptedFiles } = useDropzone({
    onDrop,
    accept: {
      'text/csv': ['.csv'],
    },
    multiple: !fileMode,
  });

  const groupFilesByFolder = (files: File[]) => {
    const folders: Record<string, File[]> = {};

    files.forEach(file => {
      const pathParts = (file as any).webkitRelativePath?.split('/') || [file.name];
      const folderName = pathParts[0];

      if (!folders[folderName]) {
        folders[folderName] = [];
      }
      folders[folderName].push(file);
    });

    return folders;
  };

  const removeFile = (fileToRemove: File) => {
    const remainingFiles = acceptedFiles.filter(file => file !== fileToRemove);
    onFilesSelected(remainingFiles);
  };

  const clearAllFiles = () => {
    onFilesSelected([]);
  };

  return (
    <Box sx={{ width: '100%' }}>
      {/* Mode Switches */}
      <Stack direction="row" spacing={2} sx={{ mb: 2 }}>
        <FormControlLabel
          control={
            <Switch
              checked={dateMode}
              onChange={(e) => setDateMode(e.target.checked)}
              color="secondary"
            />
          }
          label="üìÖ Tarih Modu (√áoklu Klas√∂r)"
        />
        <FormControlLabel
          control={
            <Switch
              checked={fileMode}
              onChange={(e) => setFileMode(e.target.checked)}
              color="warning"
            />
          }
          label="üìÑ Dosya Modu (Tek CSV)"
        />
      </Stack>

      {/* Upload Area */}
      <Paper
        {...getRootProps()}
        sx={{
          p: 3,
          textAlign: 'center',
          cursor: 'pointer',
          border: '2px dashed',
          borderColor: isDragActive ? 'primary.main' : 'grey.300',
          backgroundColor: isDragActive ? 'primary.50' : 'background.paper',
          transition: 'all 0.2s ease',
          '&:hover': {
            borderColor: 'primary.main',
            backgroundColor: 'primary.50',
          },
        }}
      >
        <input {...getInputProps()} />

        <CloudUpload sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />

        <Typography variant="h6" gutterBottom>
          {fileMode ? 'CSV Dosyasƒ± Se√ß' : 'CSV Dosyalarƒ± Se√ß'}
        </Typography>

        <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
          {fileMode 
            ? 'Tek bir CSV dosyasƒ± se√ßin'
            : dateMode 
              ? 'Klas√∂r yapƒ±sƒ± ile CSV dosyalarƒ±nƒ± s√ºr√ºkleyin'
              : 'CSV dosyalarƒ±nƒ± s√ºr√ºkleyin veya tƒ±klayƒ±n'
          }
        </Typography>

        <Alert severity="info" sx={{ mt: 2 }}>
          <Typography variant="body2">
            {fileMode 
              ? '‚Ä¢ Tek CSV dosyasƒ± i≈üleme modu'
              : dateMode 
                ? '‚Ä¢ √áoklu klas√∂r i≈üleme modu (tarih bilgisi ile)'
                : '‚Ä¢ Tek klas√∂r i≈üleme modu'
            }
          </Typography>
        </Alert>
      </Paper>

      {/* Selected Files */}
      {acceptedFiles.length > 0 && (
        <Box sx={{ mt: 2 }}>
          <Stack direction="row" justifyContent="space-between" alignItems="center" sx={{ mb: 1 }}>
            <Typography variant="subtitle2" color="primary">
              Se√ßilen Dosyalar ({acceptedFiles.length})
            </Typography>
            <IconButton
              size="small"
              onClick={clearAllFiles}
              color="error"
              title="T√ºm dosyalarƒ± temizle"
            >
              <Delete />
            </IconButton>
          </Stack>

          <Stack spacing={1}>
            {acceptedFiles.map((file, index) => (
              <Chip
                key={index}
                icon={<FileCopy />}
                label={`${file.name} (${(file.size / 1024).toFixed(1)} KB)`}
                onDelete={() => removeFile(file)}
                variant="outlined"
                color="primary"
                sx={{ justifyContent: 'space-between' }}
              />
            ))}
          </Stack>

          {dateMode && (
            <Box sx={{ mt: 2 }}>
              <Typography variant="subtitle2" color="secondary" gutterBottom>
                Klas√∂r Yapƒ±sƒ±:
              </Typography>
              {Object.entries(groupFilesByFolder(Array.from(acceptedFiles))).map(([folder, files]) => (
                <Chip
                  key={folder}
                  icon={<Folder />}
                  label={`${folder} (${files.length} dosya)`}
                  variant="outlined"
                  color="secondary"
                  sx={{ m: 0.5 }}
                />
              ))}
            </Box>
          )}
        </Box>
      )}
    </Box>
  );
};
